import React, { Component } from 'react'
import {Card, CardImg,CardTitle,CardImgOverlay, CardText} from 'reactstrap'

export default class MenuComponent extends Component {
    constructor(props){
        super(props);
        this.state ={
            selectedEmploy : null
        }
    }
    onEmploySelect(staff){
        this.setState({selectedEmploy : staff })
      }
      renderEmploy(staff){
          if(staff != null){
              return(
                <div className="col-12 col-lg-4 col-sm-6">
                <CardImg width="100%" src={staff.image} alt={staff.name} />
                <CardText>Ngày nghỉ còn lại : {staff.annualLeave}</CardText>
                <CardText>Chức Vụ :Nhân Viên</CardText>
                </div>
              )
          }else{
            (<div></div>)
        }
      }
    render() {
        const menu = this.props.staffs.map((staff) => {
            return (
                <div key={staff.id} className='col-12 col-lg-4 col-sm-6'  ocClick = {() => this.onEmploySelect(staff)}>
                    <Card >                                
                      <Card border  > <b>{staff.name}</b></Card>                                                         
                    </Card>                 
                </div>
                     )
                    }
                    );
        return (
            <div className="container">
            <div className="row">
                    {menu}
                <div className="row">
                {this.renderEmploy(this.state.selectedEmploy)}
                </div>
            </div>             
        </div>
        )
    }
}
