import React, { Component } from 'react'
import {Card, CardImg,CardTitle,CardImgOverlay, CardText} from 'reactstrap'
import {dateFormat} from 'dateformat';
import './Menu.css'

export default class MenuComponent extends Component {
    constructor(props){
        super(props);
        this.state ={
            selectedEmploy : null
        }
    }
    onEmploySelect(staff){
        this.setState({selectedEmploy : staff })
      }
      renderEmploy(staff){
          if(staff != null){
              return(
                <div className="fix-left">     
                <Card>         
                <CardImg width="100%" src={staff.image} alt={staff.name} />
                <CardText>Ngày nghỉ còn lại : {staff.annualLeave}</CardText>
                <CardText >Chức Vụ :Nhân Viên</CardText>
                <CardText>Ngày Sinh : {dateFormat(staff.doB, "dd/mm/yyyy")}</CardText>
                </Card>
                </div>
              )
             
          }else{
           return (<div></div>)
        }
      }
    render() {
        const menu = this.props.staffs.map((staff) => {
            return (
                <div key={staff.id} className='col-12 col-lg-4 col-sm-6' >
                    <Card  onClick = {() => this.onEmploySelect(staff)}>                                
                      <Card border color="primary" > <b>{staff.name}</b></Card>                                                         
                    </Card>                 
                </div>
                     )
                    }
                    );
        return (
            <div className="container">
            <div className="row">
                    {menu}
                    <br/>
                    <CardText>Bấm vào tên để xem thông tin</CardText>
            <div className="row ">
                {this.renderEmploy(this.state.selectedEmploy)}
        </div>
            </div>             
        </div>
        )
    }
}
