import React, { Component } from 'react'
import {Card, CardImg,Media,CardImgOverlay} from 'reactstrap'

export default class MenuComponent extends Component {
    constructor(props){
        super(props);
        this.state ={
            selectedEmploy : null
        }
    }
    onEmploySelect(staff){
        this.setState({selectedEmploy : staff })
      }
      renderEmploy(staff){
          if(staff != null){
              return(
                <div className="col-12 col-lg-4 col-sm-6">
                <CardImg width="100%" src={staff.image} alt={staff.name} />
                <p>Ngày nghỉ còn lại : {staff.annualLeave}</p>
                <p>Chức Vụ :Nhân Viên</p>
                </div>
              )
          }else{
            (<div></div>)
        }
      }
    render() {
        const menu = this.props.staffs.map((staff) => {
            return (
                <div key={staff.id} className='col-12 col-lg-4 col-sm-6' style={{border:"2px solid black"}} >
                    <Card ocClick = {() => this.onEmploySelect(staff)}>
                    <CardImg width="100%" src={staff.image} alt={staff.name} />
                    <CardImgOverlay>
                      <Media heading >{staff.name}</Media> 
                    </CardImgOverlay>                                              
                    </Card>                 
                </div>
                     )
                    }
                    );
        return (
            <div className="container">
            <div className="row">
                    {menu}
                <div className="row">
                {this.renderEmploy(this.state.selectedEmploy)}
                </div>
            </div>             
        </div>
        )
    }
}
