import React, { Component } from 'react'
import {Card,CardText,CardImg} from 'reactstrap'
import dateFormat from 'dateformat';


export default class StaffListComponent extends Component {
    constructor(props){
        super(props);
        this.state ={
            staffSelected : null
        }
    }
    // Change state staffSelect from null to the selected staff
    onEmploySelect(staff){
        this.setState({staffSelected : staff })
      }
    renderStaff(staff){
          if(staff != null ){
              return(
                <div style={{border :"2px solid black"}}>             
                <div className="m-2">
                <CardImg src = {staff.image} alt={staff.name}/>
                <CardText><b>Họ vả tên: {staff.name} </b> </CardText>
                <CardText> Ngày sinh: {dateFormat(staff.doB, "dd/mm/yyyy")}</CardText>
                <CardText>Ngày vào công ty : {dateFormat(staff.startDate, "dd/mm/yyyy")}</CardText>
                <CardText>Phòng ban : {staff.department.name} </CardText>
                <CardText>Số ngày nghỉ còn lại : {staff.annualLeave}</CardText>
                <CardText>Số ngày tăng ca: {staff.annualLeave}</CardText>                            
                </div>
                </div>
              )            
          }else{
           return (<div></div>)
        }
      }
    render() {
        const menu = this.props.staffs.map((staff) => {
            return (
                <div key={staff.id} className='col-12 col-lg-4 col-sm-6' >
                    <Card  onClick = {() => this.onEmploySelect(staff)}>                                
                      <Card border color="primary" > <b>{staff.name}</b></Card>                                                         
                    </Card>                 
                </div>
                     )
                    }
                    );     
        return (
            <div className="container">
                <Card><h3>Bấm vào tên để xem thông tin </h3></Card>
                <div className="row">                  
                    {menu}  
                </div>    
                <div className="row m-1">           
                    {this.renderStaff(this.state.staffSelected)}                     
                </div>             
            </div>
        )
    }
}
