import React, { Component } from 'react'
import {Card,CardText,CardImg} from 'reactstrap'
import dateFormat from 'dateformat';
import './Menu.css';

export default class MenuComponent extends Component {
    constructor(props){
        super(props);
        this.state ={
            selectedEmploy : null
        }
    }
    onEmploySelect(staff){
        this.setState({selectedEmploy : staff })
      }
    
    renderEmploy(staff){
          if(staff != null ){
              return(
                <div style={{border :"2px solid black"}}>             
                <div className="m-2">
                <CardImg src = {staff.image} alt={staff.name}/>
                <p><b>Họ Và Tên: {staff.name} </b> </p>
                <p> Ngày Sinh : {dateFormat(staff.doB, "dd/mm/yyyy")}</p>
                <p>Ngày Vào Công Ty : {dateFormat(staff.startDate, "dd/mm/yyyy")}</p>
                <p>Phòng Ban : </p>
                <p>Ngày Nghỉ Còn lại : {staff.annualLeave}</p>
                <p>Ngày Làm Thêm : {staff.annualLeave}</p>
              
                
                </div>
                </div>
              )
             
          }else{
           return (<div></div>)
        }
      }
    render() {
        const menu = this.props.staffs.map((staff) => {
            return (
                <div key={staff.id} className='col-12 col-lg-4 col-sm-6' >
                    <Card  onClick = {() => this.onEmploySelect(staff)}>                                
                      <Card border color="primary" > <b>{staff.name}</b></Card>                                                         
                    </Card>                 
                </div>
                     )
                    }
                    );
        return (
            <div className="container">
                <div className="row">
                    {menu}  
                </div>    
                <div className="row m-1">              
                    {this.renderEmploy(this.state.selectedEmploy)}                     
                </div>             
            </div>
        )
    }
}
