import React, { Component } from "react";
import { Card, CardBody, CardText, CardTitle } from "reactstrap";
import dateFormat from "dateformat";

class Staff extends Component {
  constructor(props) {
    super(props);
    this.state = {
      staffSelected: null,
      depSelected: null,
    };
  }
  // render staff information in case a staff is selected, return empty div if none is selected
  renderStaff(staff) {
    if (staff != null) {
      return (
        <div>
          <Card className="mb-4">
            <CardBody>
              <CardTitle tag="h5">Họ và tên:{staff.name}</CardTitle>
              {/* Format date to more easy-to-read date format */}
              <CardText>
                Ngày sinh: {dateFormat(staff.doB, "dd/mm/yyyy")}
              </CardText>
              <CardText>
                Ngày vào công ty: {dateFormat(staff.startDate, "dd/mm/yyyy")}
              </CardText>
              <CardText onClick={() => this.onDepSelected(dep)}>Phòng ban: {staff.department.name}</CardText>
              <CardText>Số ngày nghỉ còn lại: {staff.annualLeave}</CardText>
              <CardText>Số ngày đã làm thêm: {staff.overTime}</CardText>
            </CardBody>
          </Card>
        </div>
      );
    } else {
      return <div></div>;
    }
  }

  render() {
    return <div>
        {this.renderStaff(this.props.staffSelected)}
        {/* thay doi staffSelected */}
    </div>;
  }
}

export default Staff;
