import React, { Component } from 'react'
import {Card, CardImg,CardTitle,CardImgOverlay, CardText ,Media} from 'reactstrap'
import './Menu.css'

export default class MenuComponent extends Component {
    constructor(props){
        super(props);
        this.state ={
            selectedEmploy : null
        }
    }
    onEmploySelect(staff){
        this.setState({selectedEmploy : staff })
      }
      renderEmploy(staff){
          if(staff != null){
              return(
                <div className="fix-left">             
                <Media left >
                <CardText>Ngày nghỉ còn lại : {staff.annualLeave}</CardText>
                <CardText >Chức Vụ :Nhân Viên</CardText>
                <CardText>Ngày Sinh : {staff.doB}</CardText>
                </Media>
                </div>
              )
             
          }else{
           return (<div></div>)
        }
      }
    render() {
        const menu = this.props.staffs.map((staff) => {
            return (
                <div key={staff.id} className='col-12 col-lg-4 col-sm-6'  onClick = {() => this.onEmploySelect(staff)}>
                    <Card >                                
                      <Card border color="primary" > <b>{staff.name}</b></Card>                                                         
                    </Card>                 
                </div>
                     )
                    }
                    );
        return (
            <div className="container">
            <div className="row">
                    {menu}
                   
                
                {this.renderEmploy(this.state.selectedEmploy)}
                
            </div>             
        </div>
        )
    }
}
