import React, { Component } from 'react'
import {Card, CardImg,Media} from 'reactstrap'

export default class MenuComponent extends Component {
    constructor(props){
        super(props);
        this.state ={
            selectedEmploy : null
        }
    }
    onEmploySelect(staff){
        this.setState({selectedEmploy : staff })
      }
      renderEmploy(staff){
          if(staff != null){
              return(
                  <div>{staff.name}</div>
              )
          }else{
            (<div></div>)
        }
      }
    render() {
        const menu = this.props.staffs.map((staff) => {
            return (
                <div key={staff.id} className='col-12 col-lg-4 col-sm-6' >
                    <Card>
                        <Media left middle>
                            <CardImg width="100%" src={staff.image} alt={staff.name} />                            
                        </Media>

                            <Media body className="ml-5" >
                                <Media heading >{staff.name}</Media>
                                <p>Ngày nghỉ còn lại : {staff.annualLeave}</p>
                                <p>Chức Vụ :Nhân Viên</p>
                            </Media>                      
                        </Card>
                        <br/>                       
                     <Button color="primary">Chi Tiết</Button>

              
                   <br />
                </div>
                     )
                    }
                    );
        return (
            <div className="container">
            <div className="row">
                    {menu}
                
            </div>             
        </div>
        )
    }
}
